// Вспомогательная функция для перемешивания массива (Fisher-Yates)
const shuffleArray = (array) => {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
};

// Генерация пояснения
const generateExplanation = (question, correctAnswerText) => {
  // База пояснений (можешь расширять)
  const explanations = {
    "аббревиатура ИКТ": "ИКТ расшифровывается как Информационно-коммуникационные технологии.",
    "основной функцией ИКТ": "Главная функция ИКТ — сбор, обработка и анализ данных.",
    // ... добавь остальные по желанию
  };

  for (let key in explanations) {
    if (question.toLowerCase().includes(key)) {
      return explanations[key];
    }
  }
  return `Правильный ответ: "${correctAnswerText}".`;
};

export default function ICTExam() {
  const [mode, setMode] = useState(null); // 'training' или 'exam'
  const [shuffledQuestions, setShuffledQuestions] = useState([]);
  const [currentQ, setCurrentQ] = useState(0);
  const [answers, setAnswers] = useState({}); // Структура: { id: { text: "...", isCorrect: true/false } }
  const [showResult, setShowResult] = useState(false);
  const [showExamResults, setShowExamResults] = useState(false);

  // Инициализация и перемешивание при старте
  useEffect(() => {
    if (mode) {
      // Клонируем данные и мешаем ответы внутри каждого вопроса
      // Сам порядок вопросов оставляем как в JSON (или тоже можно перемешать)
      const shuffled = QUESTIONS_DATA.map(q => ({
        ...q,
        answers: shuffleArray(q.answers)
      }));
      setShuffledQuestions(shuffled);
    } else {
      setShuffledQuestions([]);
    }
  }, [mode]);

  const question = shuffledQuestions[currentQ];
  
  // Получаем текущий ответ пользователя (объект)
  const userAnswerObj = question ? answers[question.id] : null;
  
  // Находим правильный ответ в текущем вопросе для отображения
  const correctAnswer = question ? question.answers.find(a => a.correct) : null;

  const handleAnswer = (answer) => {
    // Сохраняем объект ответа целиком
    setAnswers({
      ...answers,
      [question.id]: {
        text: answer.text,
        isCorrect: answer.correct
      }
    });

    if (mode === 'training') {
      setShowResult(true);
    }
  };

  const nextQuestion = () => {
    if (currentQ < shuffledQuestions.length - 1) {
      setCurrentQ(currentQ + 1);
      setShowResult(false);
    } else if (mode === 'exam') {
      setShowExamResults(true);
    }
  };

  const prevQuestion = () => {
    if (currentQ > 0) {
      setCurrentQ(currentQ - 1);
      setShowResult(false);
    }
  };

  const reset = () => {
    setCurrentQ(0);
    setAnswers({});
    setShowResult(false);
    setShowExamResults(false);
    setMode(null);
  };

  // Подсчет статистики через Object.values
  const correctCount = Object.values(answers).filter(a => a.isCorrect).length;
  const totalQuestions = QUESTIONS_DATA.length; // Используем исходную длину для точности

  // Рендер выбора режима
  if (!mode) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
        <div className="max-w-2xl mx-auto">
          <div className="bg-white rounded-2xl shadow-2xl p-8">
            <div className="text-center mb-8">
              <BookOpen className="w-16 h-16 mx-auto text-indigo-600 mb-4" />
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Тест по ИКТ</h1>
              <p className="text-gray-600">Выберите режим</p>
            </div>
            <div className="space-y-4">
              <button onClick={() => setMode('training')} className="w-full bg-gradient-to-r from-green-500 to-emerald-600 text-white p-6 rounded-xl hover:from-green-600 hover:to-emerald-700 transition-all shadow-lg">
                <div className="flex items-center justify-between">
                  <div className="text-left"><h3 className="text-xl font-bold">Тренировка</h3><p className="text-sm text-green-100">С подсказками</p></div>
                  <CheckCircle className="w-8 h-8" />
                </div>
              </button>
              <button onClick={() => setMode('exam')} className="w-full bg-gradient-to-r from-orange-500 to-red-600 text-white p-6 rounded-xl hover:from-orange-600 hover:to-red-700 transition-all shadow-lg">
                <div className="flex items-center justify-between">
                  <div className="text-left"><h3 className="text-xl font-bold">Экзамен</h3><p className="text-sm text-orange-100">Результат в конце</p></div>
                  <Trophy className="w-8 h-8" />
                </div>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (shuffledQuestions.length === 0) return null;

  // Экран результатов
  if (showExamResults) {
    const percentage = Math.round((correctCount / totalQuestions) * 100);
    const passed = percentage >= 70;

    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
        <div className="max-w-3xl mx-auto">
          <div className="bg-white rounded-2xl shadow-2xl p-8">
            <div className="text-center mb-8">
              {passed ? <Trophy className="w-20 h-20 mx-auto text-green-500 mb-4" /> : <AlertCircle className="w-20 h-20 mx-auto text-orange-500 mb-4" />}
              <h1 className="text-3xl font-bold text-gray-900">{passed ? 'Сдано!' : 'На пересдачу'}</h1>
              <p className="text-2xl font-bold text-indigo-600">{percentage}%</p>
            </div>
            
            <div className="grid grid-cols-3 gap-4 mb-8">
              <div className="bg-green-50 p-4 rounded text-center"><p className="text-3xl font-bold text-green-600">{correctCount}</p><p className="text-sm">Верно</p></div>
              <div className="bg-red-50 p-4 rounded text-center"><p className="text-3xl font-bold text-red-600">{totalQuestions - correctCount}</p><p className="text-sm">Ошибки</p></div>
              <div className="bg-indigo-50 p-4 rounded text-center"><p className="text-3xl font-bold text-indigo-600">{totalQuestions}</p><p className="text-sm">Всего</p></div>
            </div>

            <button onClick={reset} className="w-full bg-indigo-600 text-white py-3 rounded-lg hover:bg-indigo-700 flex justify-center gap-2">
              <RotateCcw className="w-5 h-5" /> Пройти заново
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Основной экран вопроса
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-3xl mx-auto">
        {/* Хедер */}
        <div className="bg-white rounded-t-2xl shadow-lg p-6">
          <div className="flex justify-between items-center mb-4">
            <div>
              <h2 className="text-sm font-medium text-gray-600">{mode === 'training' ? '🎯 Тренировка' : '📝 Экзамен'}</h2>
              <p className="text-2xl font-bold text-gray-900">Вопрос {currentQ + 1} / {shuffledQuestions.length}</p>
            </div>
            <button onClick={reset}><RotateCcw className="w-6 h-6 text-gray-500 hover:text-gray-700" /></button>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
            <div className="bg-indigo-600 h-full transition-all duration-300" style={{ width: `${((currentQ + 1) / shuffledQuestions.length) * 100}%` }} />
          </div>
        </div>

        {/* Карточка вопроса */}
        <div className="bg-white shadow-lg p-6 mb-4">
          <h3 className="text-xl font-bold text-gray-900 mb-6">{question.question}</h3>
          <div className="space-y-3">
            {question.answers.map((answer) => {
              // Логика стилей
              const isSelected = userAnswerObj?.text === answer.text;
              const isCorrectAnswer = answer.correct;
              
              let btnClass = 'bg-gray-50 border-2 border-gray-200 hover:border-indigo-400';
              
              if (showResult && mode === 'training') {
                if (isSelected && isCorrectAnswer) btnClass = 'bg-green-100 border-green-500';
                else if (isSelected && !isCorrectAnswer) btnClass = 'bg-red-100 border-red-500';
                else if (isCorrectAnswer) btnClass = 'bg-green-50 border-green-300';
              } else if (isSelected) {
                btnClass = 'bg-indigo-100 border-indigo-500';
              }

              return (
                <button
                  key={answer.text} // ВАЖНО: Уникальный ключ по тексту
                  onClick={() => !showResult && handleAnswer(answer)}
                  disabled={showResult && mode === 'training'}
                  className={`w-full text-left p-4 rounded-lg transition-all ${btnClass} ${showResult ? 'cursor-default' : 'cursor-pointer'}`}
                >
                  <div className="flex items-center gap-3">
                    <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${isSelected ? 'bg-indigo-600 border-indigo-600' : 'border-gray-300'}`}>
                      {isSelected && <div className="w-3 h-3 bg-white rounded-full" />}
                    </div>
                    <span className="flex-1 font-medium text-gray-800">{answer.text}</span>
                    {showResult && mode === 'training' && isCorrectAnswer && <CheckCircle className="w-5 h-5 text-green-600" />}
                    {showResult && mode === 'training' && isSelected && !isCorrectAnswer && <XCircle className="w-5 h-5 text-red-600" />}
                  </div>
                </button>
              );
            })}
          </div>

          {/* Пояснение в режиме тренировки */}
          {showResult && mode === 'training' && (
            <div className={`mt-6 p-4 rounded-lg ${userAnswerObj?.isCorrect ? 'bg-green-50 border-l-4 border-green-500' : 'bg-orange-50 border-l-4 border-orange-500'}`}>
              <div className="flex gap-3">
                {userAnswerObj?.isCorrect ? <CheckCircle className="text-green-600" /> : <AlertCircle className="text-orange-600" />}
                <div>
                  <p className="font-bold">{userAnswerObj?.isCorrect ? 'Верно!' : 'Ошибка'}</p>
                  <p className="text-sm">{generateExplanation(question.question, correctAnswer?.text)}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Навигация */}
        <div className="bg-white rounded-b-2xl shadow-lg p-4 flex gap-3">
          <button onClick={prevQuestion} disabled={currentQ === 0} className="px-6 py-3 bg-gray-100 rounded-lg hover:bg-gray-200 disabled:opacity-50 flex gap-2">
            <ChevronLeft /> Назад
          </button>
          <button onClick={nextQuestion} disabled={!userAnswerObj} className="flex-1 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 disabled:opacity-50 flex justify-center gap-2">
            {currentQ === shuffledQuestions.length - 1 ? (mode === 'exam' ? 'Завершить' : 'Финиш') : 'Далее'} <ChevronRight />
          </button>
        </div>
      </div>
    </div>
  );
}
